mapper.insert:
INSERT INTO ENTERPRICE (
    ENT_NO, ENT_NAME, ROAD_ADD, DETAIL_ADD, ENT_TYPE, 
    ENT_COMMENT, ENT_CUST, ENT_REG_DATE, ENT_TEL, ENT_CODE
)
VALUES (
    #{entNo}, #{entName}, #{roadAdd}, 
    #{detailAdd}, #{entType}, #{entComment}, #{entCust}, 
    #{entRegDate}, #{entTel}, #{entCode}
)

mapper.select:
SELECT COUNT(*) 
FROM ENTERPRICE 
WHERE ENT_NO = #{entNo}
